mapper.select:
SELECT A.tid, A.user_id, C.user_nm, A.match_id, A.result, A.bet, A.t_title, A.t_body, A.v_count, B.league, FROM_UNIXTIME(B.match_time, '%Y-%m-%d %H:%i') AS m_time, B.home_name, B.away_name, B.score, B.status, B.result 
FROM tip AS A INNER JOIN sway_match AS B ON A.match_id = B.match_id INNER JOIN user AS C ON A.user_id = C.user_id 
WHERE A.tid = #{tid};
